/* -----------------------------------------------------------------
   Seed test data (clients, contracts,
   energy_readings).  All statements run inside a single transaction
   so they either all succeed or all roll back.
------------------------------------------------------------------*/
BEGIN;

/* === 1. clients  ============================================== */
INSERT INTO clients (id, name) VALUES
    (1, 'Client A'),
    (2, 'Client B'),
    (3, 'Client C');

/* === 2. contracts  ============================================ */
/* each contract belongs to one client                            */
INSERT INTO contracts (id, client_id, is_active, created_at) VALUES
    (1, 1, TRUE, '2025-06-10'),
    (2, 2, TRUE, '2025-06-11'),
    (3, 3, TRUE, '2025-06-12'),
    (4, 1, TRUE, '2025-06-13');   -- extra contract for Client A

/* === 3. energy_readings  ====================================== */
/* each reading belongs to one contract                           */
INSERT INTO energy_readings (id, contract_id, reading_amount_kwh, created_at) VALUES
    (1, 1,  100.00, '2025-06-10'),
    (2, 2,  300.00, '2025-06-11'),
    (3, 3, 2000.00, '2025-06-12'),
    (4, 4, 2200.00, '2025-06-13');

COMMIT;

/* n8n Postgres → Execute Query node
   Deletes **only** the data that belongs to Client 1,
   following the correct order: readings → contracts → client. */
BEGIN;

/* 1. Remove every energy reading linked to any contract of Client 1 */
DELETE FROM energy_readings
WHERE contract_id IN (
    SELECT id
    FROM contracts
    WHERE client_id = 1
);

/* 2. Remove all contracts that belong to Client 1 */
DELETE FROM contracts
WHERE client_id = 1;

/* 3. Finally, remove Client 1 itself */
DELETE FROM clients
WHERE id = 1;

COMMIT;
